import React, { useEffect, useState } from "react";
import ProductListing from "./ProductListing";

const ProductListingWrapper = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Sample data
    const fetchedProducts = [
      {
        id: 1,
        name: "Apsara Pencil",
        price: 5,
        image: "https://m.media-amazon.com/images/I/61ujOZmD35L.jpg",
      },
      {
        id: 2,
        name: "Classmate Notebook",
        price: 50,
        image: "data:image/jpeg;base64,",
      },
      {
        id: 3,
        name: "Camlin Eraser",
        price: 3,
        image: "data:image/jpeg;base64,",
      },
      {
        id: 1,
        name: "Apsara Pencil",
        price: 5,
        image: "https://m.media-amazon.com/images/I/61ujOZmD35L.jpg",
      },
      {
        id: 2,
        name: "Classmate Notebook",
        price: 50,
        image: "data:image/jpeg;base64,",
      },
      {
        id: 3,
        name: "Camlin Eraser",
        price: 3,
        image: "data:image/jpeg;base64,",
      },
    ];
    setProducts(fetchedProducts);
  }, []);

  const handleDelete = (id) => {
    const updatedList = products.filter((product) => product.id !== id);
    setProducts(updatedList);
  };

  return (
    <div className="p-4">
      <ProductListing products={products} onDelete={handleDelete} />
    </div>
  );
};

export default ProductListingWrapper;



